import React from 'react';
import './ModalCart.css';


class ModalCart extends React.Component {
  constructor(props) {
    super(props);
    this.state={
        close:props.close,
        carts:props.carts,
        visitors:[],
        books:[],
        cart:{}
    }
    this.onChangeText=this.onChangeText.bind(this);
    this.addCart = this.addCart.bind(this);
  }

  componentDidMount(){
    this.setState({visitors:JSON.parse(localStorage.getItem('visitors'))});
    this.setState({books:JSON.parse(localStorage.getItem('books'))});
  }
  onChangeText(e,item){
      var cart = this.state.cart;
      var books = this.state.books;
      var visitors = this.state.visitors;
      if(item==='book'){
         books.forEach(function (book) { 
            if(book.id===e.target.value)
            cart[item]=book;
         });
         
      }
      if(item==='visitor'){
        visitors.forEach(function (visitor) { 
           if(visitor.id===e.target.value)
           cart[item]=visitor;
        });       
     }
      this.setState({cart:cart});
  }
  
  addCart(){
    var cart = this.state.cart;
    var carts = this.state.carts;
    var maxId = 0;
    carts.forEach(function(item){
      if(maxId<parseInt(item.id))
          maxId=parseInt(item.id);
    });
    cart.id=''+(maxId+1);
    carts.push(cart);
    this.setState({carts:carts});
    var books = this.state.books;
    books.forEach(function (item) {
        if(item.id===cart.book.id){
            books[books.indexOf(item)].count = parseInt(books[books.indexOf(item)].count-1)+'';
        }
    });
    localStorage.removeItem("carts")
    localStorage.setItem('carts', JSON.stringify(carts));
    localStorage.removeItem("books")
    localStorage.setItem('books', JSON.stringify(books));
    this.state.close();
  }

  render() {
      const carts = this.state.carts;
    return (
      <div className="modal">
          <div className="field-close" onClick={this.state.close}></div>
          <div className="modal-context">
              <div className="header">
                  <h3>CREATE CART</h3>
                  <span onClick={this.state.close}>X</span>
              </div>
              <div className="content">
                <label>Name:</label>
                <select value={books[0].name} onChange={(e)=>this.onChangeSelect(e)}>             
                    <option value="visitor.name">VISITOR NAME</option>
                    <option value="book.name">BOOK NAME</option>
                </select>
                <label>Phone:</label>
          
                <div>
                    <button onClick={()=>this.addCart()}>Save</button>
                </div>
              </div>
          </div>
      </div>
    );
  }
}


export default ModalCart;
