import React from 'react';
import './App.css';
import BooksBlock from './BooksBlock'
import ModalBook from './ModalBook'

var booksArray = [{
  id:"1",
  name: 'Learn JS',
  author: 'Btchanov A.A',
  year: "2019",
  publish: 'Step Academy',
  pages: "2048",
  count: "10"
},
{
  id:"2",
  name: 'Learn React JS',
  author: 'Atchanov A.A',
  year: "2019",
  publish: 'Step Academy',
  pages: "1024",
  count: "5"
}]

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state= {
      showEditBook:false,
      bookEdit:{},
      books
    }
    this.showModalBook = this.showModalBook.bind(this);
    this.closeModalBook = this.closeModalBook.bind(this);
  }

  componentDidMount(){
    if(!localStorage.getItem('books')){
       localStorage.setItem('books', JSON.stringify(booksArray));
    }
  }

  showModalBook(book){
    this.setState({bookEdit:book,showEditBook:true});
  }

  closeModalBook(){
    this.setState({showEditBook:false});
  }



  render() {
    return (
      <div>  
        {this.state.showEditBook &&                   
        <ModalBook book={JSON.parse(JSON.stringify(this.state.bookEdit))} close={this.closeModalBook}/>
        }
        <div className="app">
          <div className="header">
            <h1>LIBRARY</h1>
            <ul className="menu">
              <li>Books</li>
              <li>Visitors</li>
              <li>Cards</li>
              <li>Statistics</li>
            </ul>
          </div>
          <div className="content">
              <BooksBlock books={this.state.books} key={this.state.bookEdit} showModalBook={this.showModalBook}/>
          </div>
        </div>
      </div>
    );
  }
}


export default App;
