import React from 'react';
import './App.css';
import BooksBlock from './BooksBlock'
import VisitorsBlock from './VisitorsBlock';

var booksArray = [{
  id:"1",
  name: 'Learn JS',
  author: 'Btchanov A.A',
  year: "2019",
  publish: 'Step Academy',
  pages: "2048",
  count: "10"
},
{
  id:"2",
  name: 'Learn React JS',
  author: 'Atchanov A.A',
  year: "2019",
  publish: 'Step Academy',
  pages: "1024",
  count: "5"
}]

var visitorsArray =[{
    id:"1"
}]

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state={
      page:<BooksBlock/>,
      key:0
    }
    this.getPage = this.getPage.bind(this);
  }

  componentDidMount(){
    if(!localStorage.getItem('books')){
       localStorage.setItem('books', JSON.stringify(booksArray));
    }

    if(!localStorage.getItem('books')){
      localStorage.setItem('books', JSON.stringify(booksArray));
   }
  }
  
  getPage(page){
    this.setState({key:this.state.key+1,page:page});
  }

  render() {
    return (
      <div>  
        <div className="app">
          <div className="header">
            <h1>LIBRARY</h1>
            <ul className="menu">
              <li onClick={()=>this.getPage(<BooksBlock key={this.state.key}/>)}>Books</li>
              <li onClick={()=>this.getPage(<VisitorsBlock key={this.state.key}/>)}>Visitors</li>
              <li>Cards</li>
              <li>Statistics</li>
            </ul>
          </div>
          <div className="content">
              {this.state.page}
          </div>
        </div>
      </div>
    );
  }
}


export default App;
