import React from 'react';
import './App.css';
import BooksBlock from './BooksBlock'
import ModalBook from './ModalBook'

var booksArray = [{
  id:"1",
  name: 'Learn JS',
  author: 'Btchanov A.A',
  year: "2019",
  publish: 'Step Academy',
  pages: "2048",
  count: "10"
},
{
  id:"2",
  name: 'Learn React JS',
  author: 'Atchanov A.A',
  year: "2019",
  publish: 'Step Academy',
  pages: "1024",
  count: "5"
}]

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state= {
      showEditBook:false,
      bookEdit:{},
      books:booksArray,
      isNewBook:false
    }
    this.showModalBook = this.showModalBook.bind(this);
    this.closeModalBook = this.closeModalBook.bind(this);
    this.saveBook = this.saveBook.bind(this);
    this.deleteBook = this.deleteBook.bind(this);
    this.addBook = this.addBook.bind(this);
  }

  showModalBook(book){
    if(book.id){
    this.setState({bookEdit:book,showEditBook:true,isNewBook:false});
    }
    else
    {
    this.setState({bookEdit:book,showEditBook:true,isNewBook:true});
    }
  }

  closeModalBook(){
    this.setState({showEditBook:false});
  }



  render() {
    console.log(this.state.isNewBook)
    return (
      <div>  
        {this.state.showEditBook &&                   
        <ModalBook new={this.state.isNewBook} book={JSON.parse(JSON.stringify(this.state.bookEdit))} books/>
        }
        <div className="app">
          <div className="header">
            <h1>LIBRARY</h1>
            <ul className="menu">
              <li>Books</li>
              <li>Visitors</li>
              <li>Cards</li>
              <li>Statistics</li>
            </ul>
          </div>
          <div className="content">
              <BooksBlock books={this.state.books} showModalBook={this.showModalBook}/>
          </div>
        </div>
      </div>
    );
  }
}


export default App;
