import React from 'react';
import './ModalBook.css';


class ModalBook extends React.Component {
  constructor(props) {
    super(props);
    this.state={
        close:props.close,
        book:props.book
    }
  }

  render() {
      const book = this.state.book;
    return (
      <div className="modal">
          <div className="field-close" onClick={this.state.close}></div>
          <div className="modal-context">
              <div className="header">
                  <h3>EDIT BOOK {book.name}</h3>
                  <span onClick={this.state.close}>X</span>
              </div>
              <div className="content">
                <input type="text" onChange={(e)=>this.setState().book.name=e.target.value} value={this.state.book.name}/>
                {book.name}
              </div>
          </div>
      </div>
    );
  }
}


export default ModalBook;
