import React from 'react';
import './BooksBlock.css';
import ModalBook from './ModalBook'

class StatisticBlock extends React.Component {
  constructor(props) {
    super(props);
    this.state={
        carts:[]
    }
    this.getTopBooks = this.getTopBooks.bind(this);
  }
  componentDidMount(){
    this.setState({carts:JSON.parse(localStorage.getItem('carts'))});
  }

  getTopBooks(){
    var carts = this.state.carts;
    var cartsBook = [];

    for(var i=0;i<carts.length;i++){
        if(!cartsBook.find(i=>i.book===carts[i].book)){
            cartsBook.push(carts[i]);
        }
        for(var j=i+1;j<carts.length;j++){
            if(carts[i].book===carts[j].book){
                cartsBook.find(i=>i.book===carts[i].book).count+=1;
            }
        }
    }
    cartsBook = cartsBook.sort((item,item2)=>item.count<item2.count);
    return(
        <table>
            <caption>TOP 5 BOOK</caption>
            {cartsBook.map(item=>
            <tr>
                <td>{item.book.name}</td>
            </tr>
            )}
        </table>
    )
  }

  render() {
    const books = this.state.books;
    return (     
      <div className="books-block">
        {this.getTopBooks()};
      </div>
    );
  }
}


export default StatisticBlock;
