import React from 'react';
import './ModalBook.css';


class ModalBook extends React.Component {
  constructor(props) {
    super(props);
    this.state={
        close:props.close,
        book:props.book
    }
    this.onChangeText=this.onChangeText.bind(this);
  }

  onChangeText(e,item){
    var book = this.state.book;
    book[item]=e.target.value;
    this.setState({book:book});
  }
  render() {
      const book = this.state.book;
    return (
      <div className="modal">
          <div className="field-close" onClick={this.state.close}></div>
          <div className="modal-context">
              <div className="header">
                  <h3>EDIT BOOK {book.name}</h3>
                  <span onClick={this.state.close}>X</span>
              </div>
              <div className="content">
                <input type="text" onChange={(e)=>this.onChangeText(e,'name')} value={this.state.book.name}/>
                {book.name}
              </div>
          </div>
      </div>
    );
  }
}


export default ModalBook;
