import React from 'react';
import './App.css';
import BooksBlock from './BooksBlock'
import ModalBook from './ModalBook'

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state= {
      showEditBook:false,
      bookEdit:{}
    }
    this.showModalBook = this.showModalBook.bind(this);
    this.closeModalBook = this.closeModalBook.bind(this);
  }

  showModalBook(book){
    this.setState({book:book,showEditBook:true});
  }

  closeModalBook(){
    this.setState({showEditBook:false});
  }
  render() {
    return (
      <div>  
        {this.state.showEditBook &&                   
        <ModalBook book={this.state.bookEdit}/>
        }
        <div className="app">
          <div className="header">
            <h1>LIBRARY</h1>
            <ul className="menu">
              <li>Books</li>
              <li>Visitors</li>
              <li>Cards</li>
              <li>Statistics</li>
            </ul>
          </div>
          <div className="content">
              <BooksBlock showModalBook={this.showModalBook} closeModalBook={this.closeModalBook}/>
          </div>
        </div>
      </div>
    );
  }
}


export default App;
